CREATE TABLE monitoring_pembayaran_rutin(
    ID int NOT NULL PRIMARY KEY
   ,NAMA VARCHAR(255)
   ,NILAI_PENGADAAN_HASIL VARCHAR(255)
   ,Pembayaran_Terakhir VARCHAR(255)
   ,Status_Jatuh_Tempo VARCHAR(255)
   ,CONSTRAINT pk_monitoring_proses_monitoring_pembayaran_rutinPRIMARY KEY (ID)
);


-- Step 2: Create the sequence
CREATE SEQUENCE monitoring_pembayaran_rutin_seq
    START WITH 1
    INCREMENT BY 1
    NOCACHE
    NOCYCLE;

-- Step 3: Create the trigger
CREATE OR REPLACE TRIGGER monitoring_pembayaran_rutin_bir
BEFORE INSERT ON monitoring_pembayaran_rutin
FOR EACH ROW
BEGIN
    SELECT monitoring_pembayaran_rutin_seq.NEXTVAL INTO :NEW.ID FROM dual;
END;


