CREATE TABLE monitoring_proses_pengadaan(
    ID int NOT NULL PRIMARY KEY
   ,NAMA VARCHAR(255)
   ,KETERANGAN_JIKA_TERLAMBAT CLOB
   ,CONSTRAINT pk_monitoring_proses_MONITORING_PROSES_PENGADAANPRIMARY KEY (ID)
,NULL);


-- Step 2: Create the sequence
CREATE SEQUENCE monitoring_proses_pengadaan_seq
    START WITH 1
    INCREMENT BY 1
    NOCACHE
    NOCYCLE;

-- Step 3: Create the trigger
CREATE OR REPLACE TRIGGER monitoring_proses_pengadaan_bir
BEFORE INSERT ON monitoring_proses_pengadaan
FOR EACH ROW
BEGIN
    SELECT monitoring_proses_pengadaan_seq.NEXTVAL INTO :NEW.ID FROM dual;
END;

